import React, { useCallback } from 'react'
import { Button } from 'antd'
import PropTypes from 'prop-types'
import { useSelector } from 'react-redux'
import { UNFOLLOW_REQUEST } from '../reducers/user'

const FollowButton = ({ post }) => {
    const dispatch = useCallback
    const { me, followLoading, unfollowLoading } = useSelector((state) => state.user);
    //팔로잉 여부
    // const isFollowing = me && me.Flowwings.find((v) => v.id === post.User.id);
    const isFollowing = me?.Flowwings.find((v) => v.id === post.User.id);
    const onClickButton = useCallback(() => {
        if(isFollowing) {
            dispatch({
                type: UNFOLLOW_REQUEST,
            })
        }
    }, [isFollowing])
  return (
    <div>
        <Button loading={ followLoading || unfollowLoading } onClickButton={onClickButton}> 팔로우 </Button>
    </div>
  )
}


FollowButton.propTypes = {
    post: PropTypes.object.isRequired,
};

export default FollowButton
