import React, { useCallback, useState } from 'react'
import PropTypes from 'prop-types';
import { PlusOutlined } from '@ant-design/icons';

const PostImages = ({ images }) => {
  const [showImageZoom, setShowImagesZoom] = useState(false);
  
  const onZoom =  useCallback(() => {
    setShowImagesZoom(true);
  },[]);

  if(images.length === 1 ){
    return (
      <>
      {/* alt 시각장애인들을 위한 힌트, image 호버 시 이미지에 대한 설명 */}
      {/* role="presentation" 시각장애인들에게 사진을 클릭할 필요가 없다고 명시 */}
        <img role="presentation" src={images[0].src} alt={image[0].src} onClick={onZoom} />
        
      </>
    )
  }
  if(images.length === 2) {
    return (
      <>
        <img role="presentation" width="50%" src={images[0].src} alt={image[0].src} onClick={onZoom} />
        <img role="presentation" width="50%" src={images[1].src} alt={image[1].src} onClick={onZoom} />
      </>
    )
  }
  return (
    <>
      <div>
      <img role="presentation" width="50%" src={images[0].src} alt={image[0].src} onClick={onZoom} />
      <div
        role='presentation'
        style={{ display: 'inline-block', width: '50%', textAlign: 'center', verticalAlign: 'middle' }}
        onClick={onZoom}
      >
        <PlusOutlined/>
        <br />
        {images.length -1}
        개의 사진 더보기
      </div>
      </div>
    </>
  )
};

PostImages.propTypes = {
  images : PropTypes.arrayOf(PropTypes.object),
}

export default PostImages
