import React, { useState } from 'react'
import PropTypes from 'prop-types'
import Slick from 'react-slick'
import styled from 'styled-components';

// func`` == func() 함수 호출 문법 tagged template literal
const Overlay = styled.div`
// 화면이 꽉참
  position: fixed;
  z-index: 5000;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
`;
const Header = styled.header`
    height: 44px;
    background: white;
    position: relative;
    padding: 0;
    text-align: center;

    & h1 {
        margin: 0;
        font-size: 17px;
        color: #333;
        line-height: 44px;
    }

    & button {
        position: absolute;
        right: 0;
        top: 0;
        padding: 15px;
        line-height: 14px;
        cursor: pointer;
    }
`;

const ImagesZoom = ({ images, onClose }) => {
    const [currentSlide, setCurrentSlide] = useState(0);
  return (
    <Overlay>
        <Header>
            <h1>상세 이미지</h1>
            <button onClose={onClose}>X</button>
        </Header>
        <div>
            <div>
                <Slick         
                    initialSlide={0}
                    // 현재 슬라이드 상태값 유지
                    afterChange={(slide) => setCurrentSlide(slide)}
                    // 무한으로 넘김
                    infinite
                    arrows={false}
                    // 한번에 하나씩만 슬라이드 보여주기
                    slidesToShow={1}
                    // 한번에 하나씩만 스크롤하기
                    slidesToScroll={1}
                >
                    {images.map((v) => (
                        <div key={v.src}>
                            <img src={v.src} alt={v.src}/>
                        </div>
                    ))}
                </Slick>
            </div>
        </div>
    </Overlay>
    
  )
}

ImagesZoom.propTypes = {
    images: PropTypes.arrayOf(PropTypes.object).isRequired,
    onClose: PropTypes.func.isRequired
}

export default ImagesZoom;
