export const initialState = {
    isLoggedIn: false,
    user: null,
    signUpData: {},
    loginData: {},    
}

export const loginAction = (data) => {
    return {
        type: 'LOG_IN',
        data,
    }
}

export const logoutAction = () => {
    return {
        type: 'LOG_OUT',
    }
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOG_IN' : 
            // 다음 스테이트로 값이 변경, initial state와는 다른 객체가 생김
            return {
                    ...state,
                    isLoggedIn: true,
                    user: action.data,
                };
        
        case 'LOG_OUT' : 
            return {
                    ...state,
                    isLoggedIn: false,
                    user: null,
                };
        default:
            return state;
        
    }
};

export default reducer;
