import React, { useEffect } from 'react';
import AppLayout from '../components/AppLayout';
import { useDispatch, useSelector } from 'react-redux';
import PostForm from '../components/PostForm';
import PostCard from '../components/PostCard';
import { LOAD_POSTS_REQUEST } from '../reducers/post';

const Home = () => {
    const dispatch = useDispatch();
    const {me} = useSelector((state) => state.user);
    const {mainPosts, hasMorePosts} = useSelector((state) => state.post);

    useEffect (() => {
        dispatch({
            type: LOAD_POSTS_REQUEST,
        });
    },[]);

    useEffect(() => {
        function onScroll() {
            // scrollY: 얼마나 내렸는지
            // clientHeight: 화면 보이는 길이
            // scrollHeight: 총길이
            console.log(window.scrollY, document.documentElement.clientHeight, document.documentElement.scrollHeight);
            // 다내리면 새로운거 로딩해라
            if( window.scrollY + document.documentElement.clientHeight === document.documentElement.scrollHeight ) {
               if(hasMorePosts) {
                    dispatch({
                    type: LOAD_POSTS_REQUEST,
                });}
            }
        }
        window.addEventListener('scroll', onScroll);
        return () => {
            window.removeEventListener('scroll', onScroll); // 사용하고 메모리 관리를 위해 반드시 해지해야함
        };

    },[hasMorePosts]);

    return (
        <AppLayout>
            {/* PostForm은 로그인한 사람들에게만 보임 */}
            {me && <PostForm />}
            {/* 반복문이 바뀌지 않으면 key를 index로 써도 되나 바뀔 경우, 그러면 안됨 -> 고유 id로 지정 */}
            {mainPosts.map((post) => <PostCard key={post.id} post={post} />) }
            
        </AppLayout>
    );
}

export default Home;
