import React, { useCallback } from 'react'
import { Avatar, Button, Card} from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { LOG_OUT_REQUEST } from '../reducers/user';
import Link from 'next/link';

const UserProfile = () => {
    const dispatch = useDispatch();
    const { me, logOutLoading } = useSelector((state) => state.user)

    const onLogOut = useCallback(() => {
        // setIsLoggedIn(false);
        dispatch({
            type: LOG_OUT_REQUEST,
        });
    }, []);

  return (
    <Card
        actions={[
            <div key="twit">짹짹<br />{me.Posts.length}</div>,
            <div key="following"><Link href="/profile"><a>팔로잉<br />{me.Followings.length}</a></Link></div>,
            <div key="follower"><Link href="/profile"><a>팔로워<br />{me.Followers.length}</a></Link></div>,
        ]}
    >
        <Card.Meta
            avatar={<Avatar>{me.nickname[0]}</Avatar>}
            title={me.nickname}
        />
        <Button key="logout" onClick={onLogOut} loading={logOutLoading}>로그아웃</Button>
    </Card>
  )
}

export default UserProfile;
